// Datei kopieren$storageUid = 17;$someFileIdentifier = 'templates/images/banner.jpg';$someFolderIdentifier = 'website/images/';$storage = $storageRepository->getByUid($storageUid);// returns a t3lib_file_File object$file = $storage->getFile($someFileIdentifier);// returns a t3lib_file_File object$folder = $storage->getFolder($someFolderIdentifier);// returns the t3lib_file_File object of the new, copied file$file->copyTo($folder);// oder equivalent$folder->addCopyOfFile($file);