class A {  private static $private     = 'I am private';  protected static $protected = 'I am protected';  public static $public       = 'I am public';}class B extends A { }$accessor = function($var) {  echo self::$$var;};$accessor('public'); // Fatal error: Cannot access self:: when no class scope is active$aAccessor = $accessor->bindTo(null, 'A');$aAccessor('public');    // I am public$aAccessor('protected'); // I am protected$aAccessor('private');   // I am private$bAccessor = $accessor->bindTo(null, 'B');$bAccessor('public');    // I am public$bAccessor('protected'); // I am protected$bAccessor('private');   // Fatal error: Cannot access  property B::$private